const supabase = Noodl.Variables.supabase;

if (!supabase) throw new Error('Supabase client not found');

try {
  const { data, error } = await supabase.from('companies').select('*');

  if (error) {
    console.error(error)
    Outputs.Failure();
  } else {
    Outputs.Companies = data;
    Outputs.Success();
  }
} catch (error) {
    console.error(error)
  Outputs.Failure();
}

if (Boolean(Noodl.Variables.supabase)) {
    console.log("Supabase client is already set up")
    return //stops if the client already exists
    }

const supabaseUrl = Inputs.supabaseUrl
const supabaseKey = Inputs.supabaseKey
try {
    const supabase = window.createClient(supabaseUrl, supabaseKey)
    Noodl.Variables.supabase = supabase

    console.log("[setup - client] Supabase client is now set up")
    Outputs.Success()
} catch (e){
    console.error("[setup - client] error initializing Supabse client: ",e)
    Outputs.Failure()
}

