try {

    let userProfileTable = Noodl.Variables.Supabase_userProfileTableName

    const { data, error } = await Noodl.Variables.supabase
        .from(userProfileTable)
        .select()
        .eq("id",userId)

    console.log("[fetch currentUser Profile] data : ",data)

