  email: Inputs.email,
  password: Inputs.password,
})

if (error) {

  Noodl.Objects.currentUser.loggedIn = false

  switch (error.message) {
    case "Invalid login credentials":
      Outputs.error = "401"
      break;
    case "Email not confirmed":
      Outputs.error = "402"
      break;
    default:
      Outputs.error = "400"

  }
  console.error("[log in] error: ", error)
  Outputs.Failure()

} else {

  Outputs.data = data
  Outputs.error = "200"
  Noodl.Objects.currentUser.loggedIn = true
  Noodl.Objects.currentUser.auth = data.user
  Noodl.Objects.currentUser.session = data.session
